<template>
  <tr>
    <th>{{ name }}</th>
    <td text-right>{{ value }}</td>
    <td><stat-bar :value='value'></stat-bar></td>
  </tr>
</template>

<script>
  import StatBar from './StatBar.vue';
  const NAMES = {
    'hp': 'hp',
    'attack': 'atk',
    'defense': 'def',
    'special-attack': 'sp atk',
    'special-defense': 'sp def',
    'speed': 'spd',
  };
  export default {
    props: ['stat', 'all'],
    computed: {
      name() {
        return NAMES[this.stat];
      },
      value() {
        return this.all.find((stat) => stat.stat.name === this.stat).base_stat;
      },
    },
    components: { StatBar },
  };
</script>